sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		return;
	}

	var request = g_form.getValue('u_request_type');
	var whatneeded = g_form.getValue('u_what_needed');

	switch (request){
		case 'legal':
		g_form.setValue('assigned_to','Charlie Whitherspoon');
			break;
		case 'hr':
			g_form.setValue('assigned_to','Beth Anglin');
			break;
		case 'facilities':
			g_form.setValue('assigned_to','Howard Johnson');
			break;
		default:
			g_form.setValue('assigned_to','');
			break;
	}

	if(whatneeded == 'other'){
		g_form.setValue('assigned_to','Fred Luddy');
	}

/*	if(request =='legal'){
		g_form.setValue('assigned_to','Charlie Whitherspoon');
	}
	if(request =='hr'){
		g_form.setValue('assigned_to','Beth Anglin');
	}
	if(request =='facilities'){
		g_form.setValue('assigned_to','Howard Johnson');
	}

	*/
}
